import { useState } from "react";
import { useEffect } from "react";

export const Products = () => {
    const [products, setProducts] = useState([]);
    
    useEffect(() => {
        fetch('../api/products.js')
            .then((res) => res.json())
            .then(data => setProducts(data)) 
    }, [])

    return(
        <div className="item__container">
            {
                products.map((product) => (
                <div key={product.id} className="card">
                    <div className="card__image"><img src={product.imagen} alt="Foto del artÃ­culo" /></div>
                    <h3 className="card_title">{product.nombre}</h3>
                    <div><span className="card__detail">{product.tag}</span></div>
                    <span className="card__price">${product.precio}</span>
                </div>))
            }
        </div>
    )
}
